}

.dropbox-dropin-default:hover, .dropbox-dropin-error:hover {
  border-color: #dedede;
  border-bottom-color: #cacaca;
  background: #fdfdfd;
background: -moz-linear-gradient(top, #fdfdfd 0%, #f5f5f5 100%);
